select distinct a.CNTRCT_SYS_ID, a.CNTRCT_ID,a.CNTRCT_TYPE,a.CNTRCT_VRSN_NBR,a.CNTRCT_STTS_CD, b.DT_SGMNT_EFCTV_DT,b.DT_SGMNT_EXPR_DT,b.DT_SGMNT_SYS_ID,b.PROD_SYS_ID, a.CNTRCT_TEST_PROD_MFDF_IND,b.DT_SGMNT_TEST_PROD_IND,b.DT_SGMNT_STTS_CD,b.DT_SGMNT_VRSN_NBR from CMST_CNTRCT_MSTR a,CNTRCT_DT_SGMNT b ,CNTRCT_DT_SGMNT_ASSN c where b.DT_SGMNT_SYS_ID = c.DT_SGMNT_SYS_ID and c.CNTRCT_SYS_ID = a.CNTRCT_SYS_ID and a.CNTRCT_ID = {0}
and b.DT_SGMNT_EFCTV_DT = TO_DATE({1},'mm/dd/yyyy') AND a.CNTRCT_VRSN_NBR < {2}
ORDER BY a.CNTRCT_VRSN_NBR desc ,b.DT_SGMNT_EFCTV_DT asc


