DataSource.SelectSQLQuery:
SELECT DBMS_RANDOM.random AS keyval , d.prmry_cd AS term , e.cd_desc_txt AS qualifier , g.prmry_cd AS pva , h.dtyp_data_type_lgnd , c.bnft_lvl_extrnl_rfrnc_id AS REFERENCE, a.BNFT_LINE_CODED_VAL , b.blvl_bnft_lvl_desc , i.note_id , i.note_nm , a.blvl_bnft_lvl_id FROM (SELECT A.DT_SGMNT_SYS_ID DT_SGMNT_SYS_ID , A.PROD_SYS_ID PROD_SYS_ID , B.BNFT_CMPNT_SYS_ID BNFT_CMPNT_SYS_ID , B.BNFT_SYS_ID BNFT_SYS_ID , B.BLVL_BNFT_LVL_ID BLVL_BNFT_LVL_ID , B.BLN_BNFT_LINE_ID BLN_BNFT_LINE_ID , C.BNFT_DEFN_SYS_ID BNFT_DEFN_SYS_ID , B.BNFT_LINE_CODED_VAL BNFT_LINE_CODED_VAL , B.BLVL_BNFT_QLFR_FRQNCY BLVL_BNFT_QLFR_FRQNCY, B.BLVL_BNFT_LVL_DESC BLVL_BNFT_LVL_DESC , C.BNFT_HIDE_FLAG BNFT_HIDE_FLAG , C.BNFT_CMPNT_HIDE_FLAG BNFT_CMPNT_HIDE_FLAG , B.LST_CHG_TMS LST_CHG_TMS , B.LST_CHG_USR LST_CHG_USR , B.CREATD_TM_STMP CREATD_TM_STMP , B.CREATD_USER_ID CREATD_USER_ID FROM CNTRCT_DT_SGMNT A INNER JOIN CNTRCT_BNFT_CODED_LINES B ON (A.DT_SGMNT_SYS_ID = B.DT_SGMNT_SYS_ID) INNER JOIN CNTRCT_BNFT_ASSN C ON(B.DT_SGMNT_SYS_ID = C.DT_SGMNT_SYS_ID AND B.BNFT_CMPNT_SYS_ID = C.BNFT_CMPNT_SYS_ID AND B.BNFT_SYS_ID = C.BNFT_SYS_ID) WHERE a.dt_sgmnt_sys_id = {0} AND B.BNFT_CMPNT_SYS_ID = {1} AND B.BNFT_SYS_ID = {2}
UNION ALL
SELECT A.DT_SGMNT_SYS_ID DT_SGMNT_SYS_ID , A.PROD_SYS_ID PROD_SYS_ID , C.BNFT_CMPNT_SYS_ID BNFT_CMPNT_SYS_ID , C.BNFT_SYS_ID BNFT_SYS_ID , C.BLVL_BNFT_LVL_ID BLVL_BNFT_LVL_ID , C.BLN_BNFT_LINE_ID BLN_BNFT_LINE_ID , C.BNFT_DEFN_SYS_ID BNFT_DEFN_SYS_ID , NULL BNFT_LINE_CODED_VAL , C.BLVL_BNFT_FRQNCY_QLFR BLVL_BNFT_QLFR_FRQNCY, C.BLVL_BNFT_LVL_DESC BLVL_BNFT_LVL_DESC , D.BNFT_HIDE_FLAG BNFT_HIDE_FLAG , D.BNFT_CMPNT_HIDE_FLAG BNFT_CMPNT_HIDE_FLAG , C.LST_CHG_TMS LST_CHG_TMS , C.LST_CHG_USR LST_CHG_USR , C.CREATD_TM_STMP CREATD_TM_STMP , C.CREATD_USER_ID CREATD_USER_ID FROM CNTRCT_DT_SGMNT A INNER JOIN PROD_BNFT_CSTMZD C ON (A.PROD_SYS_ID = C.PROD_SYS_ID) INNER JOIN CNTRCT_BNFT_ASSN D ON(D.DT_SGMNT_SYS_ID = a.DT_SGMNT_SYS_ID AND D.BNFT_CMPNT_SYS_ID = C.BNFT_CMPNT_SYS_ID AND D.BNFT_SYS_ID = C.BNFT_SYS_ID) WHERE c.bnft_hide_flag = 'F' AND c.bnft_line_hide_flag = 'F' AND c.bnft_lvl_hide_flag = 'F' AND NOT EXISTS (SELECT 1 FROM CNTRCT_BNFT_CODED_LINES WHERE DT_SGMNT_SYS_ID = A.DT_SGMNT_SYS_ID AND bln_bnft_line_id = c.bln_bnft_line_id ) AND a.dt_sgmnt_sys_id = {0} AND C.BNFT_CMPNT_SYS_ID = {1} AND C.BNFT_SYS_ID = {2} ) a , blvl_bnft_lvl b , bln_bnft_line c , (SELECT prmry_cd , cd_desc_txt FROM item WHERE ctlg_id = 1934 ) d , (SELECT prmry_cd , cd_desc_txt FROM item WHERE ctlg_id = 1935 ) e , (SELECT prmry_cd , cd_desc_txt FROM item WHERE ctlg_id = 1936 ) g , dtyp_data_type h , (SELECT a.scndry_enty_id AS note_benline_id, a.note_id , a.note_vrsn_nbr , b.note_nm , a.bnft_cmpnt_sys_id AS bcid , a.prmry_enty_id AS dgid FROM note_domain_ovrd_val a, nmst_note_mstr b WHERE a.prmry_enty_id = {0} AND a.prmry_enty_type = 'ATTACHCONTRACT' AND a.scndry_enty_type = 'ATTACHBNFTLINE' AND a.note_id = b.note_id AND a.note_vrsn_nbr = b.note_vrsn_nbr ) i WHERE a.bnft_defn_sys_id = b.bnft_defn_sys_id AND a.blvl_bnft_lvl_id = b.blvl_bnft_lvl_id AND a.bnft_defn_sys_id = c.bnft_defn_sys_id AND a.bln_bnft_line_id = c.bln_bnft_line_id AND a.blvl_bnft_lvl_id = c.blvl_bnft_lvl_id AND h.dtyp_data_type_id = c.dtyp_data_type_id AND b.blvl_bnft_lvl_term = d.prmry_cd(+) -- Term AND b.blvl_bnft_lvl_qlfr = e.prmry_cd(+) -- Qualifier AND c.bln_bnft_line_pva = g.prmry_cd -- Provider Arrangement AND c.bln_bnft_line_id = i.note_benline_id(+) AND i.bcid(+) = {1} AND i.dgid(+) = {0} ORDER BY a.blvl_bnft_lvl_id

SELECT dbms_random.random as keyval,A.CNTRCT_TIER_SYS_ID, D.tier_cd, D.data_typ, C.crtria_nm, C.dsply_sqnc_nbr, B.crtria_val
FROM CNTRCT_TIER_MSTR A 
INNER JOIN CNTRCT_TIER_CRTRIA_VAL B ON (B.CNTRCT_TIER_SYS_ID = A.CNTRCT_TIER_SYS_ID) 
INNER JOIN TIER_CRTRIA C ON (C.TIER_CRTRIA_SYS_ID = B.TIER_CRTRIA_SYS_ID)
INNER JOIN TIER_DEFN D ON (D.TIER_DEFN_SYS_ID = C.TIER_DEFN_SYS_ID)
INNER JOIN CNTRCT_DT_SGMNT_ASSN E ON (E.dt_sgmnt_sys_id = A.dt_sgmnt_sys_id)
WHERE 	A.dt_sgmnt_sys_id = {0}
AND 	A.bnft_cmpnt_sys_id={1}
AND 	A.bnft_sys_id = {2} 
ORDER BY D.tier_cd, A.CNTRCT_TIER_SYS_ID
