SELECT * FROM (SELECT BENEFIT_ID,BENEFIT_NM FROM ( SELECT BENEFITS_ST.BENEFIT_ID,BENEFITS_ST.BENEFIT_NM FROM ( SELECT BFT_MSTR.BNFT_SYS_ID BENEFIT_ID,BFT_MSTR.BNFT_ID_NM BENEFIT_NM,BFT_MSTR.BNFT_PARNT_SYS_ID BNFT_PARNT_ID, (CASE WHEN BEN_COMP.BNFT_CMPNT_ST_CD IS NULL THEN 'TRUE' ELSE (CASE WHEN BEN_COMP.BNFT_CMPNT_ST_CD IN (SELECT MDT_ST.BMNDT_ST_CD FROM BMNDT_STCD_INFO MDT_ST, BMNDT_BNFT_MNDT BN_MD WHERE BN_MD. BNFT_MNDT_SYS_ID = MDT_ST. BNFT_MNDT_SYS_ID AND BN_MD.BNFT_SYS_ID = BFT_MSTR.BNFT_SYS_ID ) THEN 'TRUE' ELSE 'FALSE' END ) END) MUL_STATES FROM ( SELECT A.BNFT_SYS_ID BFT_ID, A.BNFT_ID_NM BFT_NM,C.BNFT_CMPNT_SYS_ID BFT_CMP_ID FROM BMST_BNFT_MSTR A , BDM_BNFT_DEFN_MSTR F,BCM_BNFT_CMPNT_MSTR C, (SELECT BNFT_PARNT_SYS_ID, MAX(BNFT_VRSN_NBR) BNFT_VRSN_NBR FROM BMST_BNFT_MSTR X where BNFT_STTS_CD = 'PUBLISHED' GROUP BY BNFT_PARNT_SYS_ID) AA WHERE A.BNFT_PARNT_SYS_ID = AA.BNFT_PARNT_SYS_ID AND A.BNFT_VRSN_NBR = AA.BNFT_VRSN_NBR AND F.BNFT_SYS_ID = A.BNFT_SYS_ID AND A.BNFT_TYPE = C.BNFT_CMPNT_TYPE AND NVL(A.MNDT_TYPE,'NULL') = NVL(C.BNFT_CMPNT_MDNT_TYPE,'NULL') AND (C.BNFT_CMPNT_EFCTV_DT) >= (F.BNFT_DEFN_EFCTV_DT) AND (F.BNFT_DEFN_EXPIRY_DT) >= (C.BNFT_CMPNT_EXPIRY_DT) AND C.BNFT_CMPNT_SYS_ID = {0} AND A.BNFT_STTS_CD = 'PUBLISHED' AND A.BNFT_PARNT_SYS_ID IN (
select y.bnft_parnt_sys_id from bmst_bnft_mstr Y where (y.bnft_parnt_sys_id, y.bnft_vrsn_nbr) in ( SELECT bnft_parnt_sys_id, MAX(BNFT_VRSN_NBR) BNFT_VRSN_NBR FROM BMST_BNFT_MSTR X where BNFT_STTS_CD = 'PUBLISHED' GROUP BY BNFT_PARNT_SYS_ID ) and DMNVLD('BENEFITCOMP',c.bnft_cmpnt_parnt_sys_id,'stdbenefit',y.bnft_parnt_sys_id )='T' )
) benefits, BCM_BNFT_CMPNT_MSTR BEN_COMP, BMST_BNFT_MSTR BFT_MSTR WHERE BEN_COMP.BNFT_CMPNT_SYS_ID = benefits.BFT_CMP_ID AND BFT_MSTR.BNFT_SYS_ID = benefits.BFT_ID ) BENEFITS_ST WHERE BENEFITS_ST.MUL_STATES = 'TRUE'
