select 	distinct a.BLVL_BNFT_LVL_TERM as term, a.blvl_bnft_lvl_qlfr as qualifier, a.blvl_bnft_lvl_desc as description
			from 	blvl_bnft_lvl a, item b 
			where 	a.blvl_bnft_lvl_id = {0} and a.BNFT_DEFN_SYS_ID = {1}


select B.CNTRCT_SYS_ID,A.DT_SGMNT_SYS_ID  from CNTRCT_DT_SGMNT A,
CNTRCT_DT_SGMNT_ASSN B where B.DT_SGMNT_SYS_ID= A.DT_SGMNT_SYS_ID
and A.PROD_SYS_ID = {0}

SelectSQLQuery.STATIC:

	    select a.CNTRCT_SYS_ID,b.DT_SGMNT_SYS_ID from CMST_CNTRCT_MSTR a ,CNTRCT_DT_SGMNT b ,CNTRCT_DT_SGMNT_ASSN c
		where a.CNTRCT_SYS_ID {0} 
		and b.DT_SGMNT_SYS_ID {7}
		and c.DT_SGMNT_SYS_ID =b.DT_SGMNT_SYS_ID			
        and  a.CNTRCT_SYS_ID=c.CNTRCT_SYS_ID(+)


select 	a.CNTRCT_ID, a.CNTRCT_TYPE, a.CNTRCT_STTS_CD, b.DT_SGMNT_EFCTV_DT,b.DT_SGMNT_EXPR_DT,a.CNTRCT_VRSN_NBR
from 	CMST_CNTRCT_MSTR a,CNTRCT_DT_SGMNT b ,CNTRCT_DT_SGMNT_ASSN c 
