DataSource.SelectSQLQuery:
SELECT mgrtd_dt_sgmnt_sys_id, MGRT_NOTE_FLAG	 
  FROM mgrtd_cntrct_dt_sgmnt_mstr
 WHERE mgrtd_dt_sgmnt_sys_id = {0}

select dbms_random.random,max(A.PROD_PARNT_SYS_ID) FROM PROD_MSTR A, MGRTD_CNTRCT_DT_SGMNT_MSTR B
WHERE A.PROD_SYS_ID = B.EWPD_PROD_SYS_ID
AND B.MGRTD_CNTRCT_SYS_ID = {0}

SELECT
	d.mgrtd_dt_sgmnt_sys_id, d.cvrg_cd, d.prcg_cd, d.ntwk_cd 
FROM
	mgrtd_cntrct_prcg_info d
WHERE 
	d.mgrtd_dt_sgmnt_sys_id= {0}
	AND d.cvrg_cd = {1}
	AND d.prcg_cd = {2}
	AND d.ntwk_cd = {3}
