DataSource.SelectSQLQuery:
SELECT 	A.BNFT_SYS_ID FROM 	BMST_BNFT_MSTR A 
		WHERE	A.BNFT_PARNT_SYS_ID != {0} and
		A.BNFT_ID_NM = {1}

			SELECT A.BNFT_SYS_ID
                  FROM 	BMST_BNFT_MSTR A 
                  WHERE	A.BNFT_PARNT_SYS_ID != {4} 
                  AND 
                  A.BNFT_PARNT_SYS_ID in 
                  (
                  select B.ENTY_SYS_ID from ENTY_DOMN_INFO B, AVLBL_DOMN C
                  where 
                  C.BUS_GRP_NM {3}
                  AND C.BUS_ENTY_NM {2}
                  AND C.LOB_NM {1}
                  AND C.MRKT_BUS_UNT {5}
                  AND B.DOMN_SYS_ID = C.DOMN_SYS_ID 
                  AND B.ENTY_TYPE = 'stdbenefit'    
                  AND B.ENTY_NM = {0}
                  )

SELECT	A.BNFT_SYS_ID, A.BNFT_ID_NM, A.BNFT_DESC_TXT, A.BNFT_VRSN_NBR, A.BNFT_STTS_CD, A.BNFT_PARNT_SYS_ID, 
		A.LST_CHG_USR, A.LST_CHG_TMS, A.CREATD_USER_ID, A.CREATD_TM_STMP, A.BNFT_TYPE, A.MNDT_TYPE, A.BNFT_ST_CD, B.CD_DESC_TXT
		,a.BNFT_CTGRY
from 
BMST_BNFT_MSTR A, ITEM B where A.BNFT_PARNT_SYS_ID in 
(
    select B.ENTY_SYS_ID from ENTY_DOMN_INFO B, AVLBL_DOMN C
    where 
    C.BUS_GRP_NM  {0}
    AND C.BUS_ENTY_NM  {1}
    AND C.LOB_NM  {2} 
	AND C.MRKT_BUS_UNT {4}
    AND B.DOMN_SYS_ID = C.DOMN_SYS_ID 
    AND B.ENTY_TYPE='stdbenefit'    
    AND B.ENTY_NM= {3}   
  )
  and A.BNFT_VRSN_NBR = (select max(D.BNFT_VRSN_NBR) 
  from BMST_BNFT_MSTR D 
  where A.BNFT_PARNT_SYS_ID = D.BNFT_PARNT_SYS_ID and D.BNFT_STTS_CD != 'MARKED_FOR_DELETION')
	AND (a.BNFT_CTGRY = b.PRMRY_CD(+) AND b.CTLG_ID(+) = 2222)

SELECT	A.BNFT_SYS_ID, A.BNFT_ID_NM, A.BNFT_DESC_TXT, A.BNFT_VRSN_NBR, A.BNFT_STTS_CD, A.BNFT_PARNT_SYS_ID,
		A.LST_CHG_USR, A.LST_CHG_TMS, A.CREATD_USER_ID, A.CREATD_TM_STMP, A.BNFT_TYPE, A.MNDT_TYPE, A.BNFT_ST_CD, B.CD_DESC_TXT,a.BNFT_CTGRY
from 
BMST_BNFT_MSTR A,ITEM B where A.BNFT_PARNT_SYS_ID in 
(
    select B.ENTY_SYS_ID from ENTY_DOMN_INFO B, AVLBL_DOMN C
    where 
    C.BUS_GRP_NM  {0}
    AND C.BUS_ENTY_NM  {1}
    AND C.LOB_NM  {2} 
	AND C.MRKT_BUS_UNT {4}
    AND B.DOMN_SYS_ID = C.DOMN_SYS_ID 
    AND B.ENTY_TYPE='stdbenefit'    
    AND B.ENTY_NM= {3}   
  )
  and A.BNFT_VRSN_NBR = (select max(D.BNFT_VRSN_NBR) 
  from BMST_BNFT_MSTR D 
  where A.BNFT_PARNT_SYS_ID = D.BNFT_PARNT_SYS_ID)
	AND (a.BNFT_CTGRY = b.PRMRY_CD(+) AND b.CTLG_ID(+) = 2222)

SelectSQLQuery.STATIC:
     SELECT * FROM (SELECT BNFT_SYS_ID, BNFT_ID_NM, BNFT_DESC_TXT, BNFT_VRSN_NBR, BNFT_PARNT_SYS_ID, BNFT_STTS_CD FROM  BMST_BNFT_MSTR 
	    WHERE BMST_BNFT_MSTR.BNFT_SYS_ID IN ( 
		  SELECT MAX(BNFT_SYS_ID) FROM  BMST_BNFT_MSTR MSTR WHERE MSTR.BNFT_PARNT_SYS_ID IN (
			      SELECT ED.ENTY_SYS_ID FROM ENTY_DOMN_INFO ED WHERE ED.ENTY_TYPE = 'stdbenefit' AND ED.DOMN_SYS_ID IN (
					  SELECT D.DOMN_SYS_ID FROM AVLBL_DOMN D WHERE  D.LOB_NM  {0}  
								  AND D.BUS_GRP_NM  {1}  
								  AND D.BUS_ENTY_NM {2}
								  AND D.MRKT_BUS_UNT {10}
				    )
			)

SelectSQLQuery.DYNAMIC:
  AND
	      MSTR.BNFT_SYS_ID IN (
	      SELECT U.ENTY_SYS_ID FROM  BNFT_BSIC_SLCTD_INFO U where U.SLCTD_ITEM_TYPE = 'qualifier' 
	      AND U.SLCTD_ITEM_CD  {3} 
	      )

  AND
	      MSTR.BNFT_SYS_ID IN (
	      SELECT U.ENTY_SYS_ID FROM  BNFT_BSIC_SLCTD_INFO U where U.SLCTD_ITEM_TYPE = 'term' 
	      AND U.SLCTD_ITEM_CD  {4}
	      )

  AND
	      MSTR.BNFT_SYS_ID IN (
	      SELECT U.ENTY_SYS_ID FROM  BNFT_BSIC_SLCTD_INFO U where U.SLCTD_ITEM_TYPE = 'provider arrangement' 
	      AND U.SLCTD_ITEM_CD  {5}
	      )

DataSource.SelectSQLQuery:
                 select blvl_bnft_lvl_term from blvl_bnft_lvl a, bdm_bnft_defn_mstr b 
				where b.bnft_defn_sys_id=a.bnft_defn_sys_id
				and b.bnft_sys_id= {0}
				and a.blvl_bnft_lvl_term not in (
					select blvl_bnft_lvl_term from bnft_ln_term_mapg 
					)
