DataSource.SelectSQLQuery:
SELECT  ADQM_ADMN_QSTN_NBR, ADMIN_OPT_SYS_ID, SQNC_NBR
FROM 	ADMIN_OPT_TO_QSTN_ASSN 
WHERE 	ADMIN_OPT_SYS_ID = {0} and ADQM_ADMN_QSTN_NBR = {1}

 SELECT 'Y' AS IS_ADMIN_OPTION_EXCLUDED FROM ADMIN_OPT_MSTR A, ITEM B,CATALOG C
WHERE A.ADMIN_OPT_DESC_TXT =B.CD_DESC_TXT AND B.CTLG_ID =C.CTLG_ID AND C.CTLG_NAME='TIERING EXCLUDED ADMIN OPTIONS'
AND A.ADMIN_OPT_SYS_ID={0}

SELECT G.ADMIN_LVL_OPT_ASSN_SYS_ID, A.QSTNR_HRCHY_SYS_ID, A.ADQM_ADMN_QSTN_NBR, A.PRNT_QSTNR_HRCHY_SYS_ID, A.RFRNC_ID, B.OVRD_ANSWR_ID, D.ADQM_ADMN_QSTN_DESC, E.PSBL_ANSWR, F.CD_DESC_TXT, D.PRVDR_ARNGMNT, G.BNFT_DEFN_SYS_ID, B.CNTRCT_TIER_SYS_ID, CASE (SELECT 'Y' FROM TIER_NOTE_OVRD_VAL t, BNFT_ADMIN_TO_LVL_ASSN a WHERE t.scndry_enty_id = a.admin_lvl_opt_assn_sys_id AND t.prmry_enty_type ='ATTACHCONTRACT' AND t.prmry_enty_id = {1} AND scndry_enty_id = {5} AND scndry_enty_type ='ATTACHQUESTION' AND adqm_admn_qstn_nbr=D.ADQM_ADMN_QSTN_NBR AND bnft_cmpnt_sys_id = {3} AND a.bnft_admin_sys_id = {0} AND t.bnft_defn_sys_id =a.bnft_defn_sys_id AND TIER_SYS_ID = B.CNTRCT_TIER_SYS_ID AND a.admin_lvl_opt_assn_sys_id = t.scndry_enty_id ) WHEN 'Y' THEN 'Y' ELSE 'N' END AS NOTE_EXIST, DECODE( (SELECT COUNT(*) CNT FROM QSTN_DOMN_ASSN WHERE DOMN_CD IN ('INFO-CC','INFO-SS','C-CNFG','C-MNUL') AND ADQM_ADMN_QSTN_NBR = D.ADQM_ADMN_QSTN_NBR ), 0, 'N','Y') VALID_DOMAIN FROM QSTNR_MSTR A, (SELECT A.QSTNR_HRCHY_SYS_ID, A.OVRD_ANSWR_ID, A.CNTRCT_TIER_SYS_ID
FROM CNTRCT_TIER_ANSWR_OVRD A
WHERE A.ADMIN_LVL_OPT_ASSN_SYS_ID = {5} AND A.DT_SGMNT_SYS_ID = {1}
AND A.BNFT_CMPNT_SYS_ID = {3}
UNION ALL
SELECT qstnr_hrchy_sys_id, psbl_answr_id, cntrct_tier_sys_id FROM (SELECT a.qstnr_hrchy_sys_id, b.psbl_answr_id, a.adqm_admn_qstn_nbr, a.cntrct_tier_sys_id FROM (SELECT a.qstnr_hrchy_sys_id, a.adqm_admn_qstn_nbr, m.cntrct_tier_sys_id FROM qstnr_mstr a, cntrct_tier_mstr m WHERE a.admin_opt_sys_id = {2} AND A.prnt_qstnr_hrchy_sys_id = A.qstnr_hrchy_sys_id and m.cntrct_tier_sys_id {4} UNION ALL SELECT b.qstnr_hrchy_sys_id, b.adqm_admn_qstn_nbr, a.cntrct_tier_sys_id FROM cntrct_tier_answr_ovrd A, qstnr_mstr b WHERE A.admin_lvl_opt_assn_sys_id = {5} AND a.bnft_cmpnt_sys_id = {3} AND a.dt_sgmnt_sys_id = {1} AND a.qstnr_hrchy_sys_id = b.prnt_qstnr_hrchy_sys_id AND a.ovrd_answr_id = b.prnt_answr_id AND b.admin_opt_sys_id = {2} ) a, psbl_answr b WHERE A.adqm_admn_qstn_nbr = b.adqm_admn_qstn_nbr AND b.psbl_answr = 'Not Answered' AND (a.cntrct_tier_sys_id, a.qstnr_hrchy_sys_id) NOT IN (SELECT a.cntrct_tier_sys_id, a.qstnr_hrchy_sys_id FROM cntrct_tier_answr_ovrd A WHERE A.admin_lvl_opt_assn_sys_id = {5} AND bnft_cmpnt_sys_id = {3} AND dt_sgmnt_sys_id = {1} ) ) a, ADQM_ADMN_QSTN_MSTR e WHERE a.adqm_admn_qstn_nbr = e.ADQM_ADMN_QSTN_NBR AND isdomainvalid ('contract', {6}, 'QUESTION',A.qstnr_hrchy_sys_id) = 'T' AND (e.PRVDR_ARNGMNT is null or e.PRVDR_ARNGMNT IN (SELECT z.pva_id FROM enty_domn_info x, avlbl_domn y, prod_fmly_buss_enty_pva_map z , cntrct_dt_sgmnt a, prod_mstr b WHERE x.enty_type = 'contract' AND x.enty_sys_id = {6} AND x.domn_sys_id = y.domn_sys_id AND y.bus_enty_nm = z.buss_enty_id AND z.prod_fmly_id = b.prod_fmly_nm AND b.prod_sys_id =A.prod_sys_id AND A.dt_sgmnt_sys_id ={1} )) ) B, ADQM_ADMN_QSTN_MSTR D,BNFT_ADMIN_TO_LVL_ASSN G, PSBL_ANSWR E, ITEM F WHERE A.QSTNR_HRCHY_SYS_ID = B.QSTNR_HRCHY_SYS_ID AND A.ADMIN_OPT_SYS_ID = G.ADMIN_OPT_SYS_ID and G.ADMIN_LVL_OPT_ASSN_SYS_ID = {5} AND D.ADQM_ADMN_QSTN_NBR = A.ADQM_ADMN_QSTN_NBR AND E.adqm_admn_qstn_nbr = A.adqm_admn_qstn_nbr AND E.PSBL_ANSWR_ID = B.OVRD_ANSWR_ID AND A.RFRNC_ID = F.PRMRY_CD(+) AND F.CTLG_ID (+) =1938 ORDER BY B.CNTRCT_TIER_SYS_ID, A.SQNC_NBR
