DataSource.SelectSQLQuery:
 SELECT 	A.BNFT_CMPNT_SYS_ID, A.BNFT_CMPNT_NM, A.BNFT_CMPNT_VRSN_NBR, A.BNFT_CMPNT_DESC_TXT, A.BNFT_CMPNT_EFCTV_DT, A.BNFT_CMPNT_EXPIRY_DT,
		A.BNFT_CMPNT_STTS_CD, A.BNFT_CMPNT_PARNT_SYS_ID, A.LST_CHG_TMS, A.LST_CHG_USR, A.CREATD_USER_ID, A.CREATD_TM_STMP, A.BNFT_CMPNT_TYPE,
		A.BNFT_CMPNT_MDNT_TYPE, A.BNFT_CMPNT_ST_CD , B.CD_DESC_TXT
FROM	BCM_BNFT_CMPNT_MSTR A, ITEM B WHERE 				
(A.BNFT_CMPNT_ST_CD = B.PRMRY_CD(+) AND B.CTLG_ID(+) = 1626) AND
A.BNFT_CMPNT_PARNT_SYS_ID IN 
		(
		    select B.ENTY_SYS_ID from ENTY_DOMN_INFO B, AVLBL_DOMN C
		    where 
		    C.BUS_GRP_NM  {0}
		    AND C.BUS_ENTY_NM  {1}
		    AND C.LOB_NM  {2}
		    AND C.MRKT_BUS_UNT  {4}
		    AND B.DOMN_SYS_ID = C.DOMN_SYS_ID 
		    AND B.ENTY_TYPE='BENEFITCOMP'    
		    AND B.ENTY_NM= {3}   
		  )
			AND A.BNFT_CMPNT_VRSN_NBR = (SELECT MAX(D.BNFT_CMPNT_VRSN_NBR)
			FROM BCM_BNFT_CMPNT_MSTR D
			WHERE A.BNFT_CMPNT_PARNT_SYS_ID = D.BNFT_CMPNT_PARNT_SYS_ID	AND D.BNFT_CMPNT_STTS_CD != 'MARKED_FOR_DELETION')

SELECT 	A.BNFT_CMPNT_SYS_ID, A.BNFT_CMPNT_NM, A.BNFT_CMPNT_VRSN_NBR, A.BNFT_CMPNT_DESC_TXT, A.BNFT_CMPNT_EFCTV_DT, A.BNFT_CMPNT_EXPIRY_DT,
		A.BNFT_CMPNT_STTS_CD, A.BNFT_CMPNT_PARNT_SYS_ID, A.LST_CHG_TMS, A.LST_CHG_USR, A.CREATD_USER_ID, A.CREATD_TM_STMP
FROM	BCM_BNFT_CMPNT_MSTR A WHERE A.BNFT_CMPNT_PARNT_SYS_ID IN 
		(
		    select B.ENTY_SYS_ID from ENTY_DOMN_INFO B, AVLBL_DOMN C
		    where 
		    C.BUS_GRP_NM  {0}
		    AND C.BUS_ENTY_NM  {1}
		    AND C.LOB_NM  {2}
			AND C.MRKT_BUS_UNT {4} 
		    AND B.DOMN_SYS_ID = C.DOMN_SYS_ID 
		    AND B.ENTY_TYPE='BENEFITCOMP'    
		    AND B.ENTY_NM= {3}   
		  )
			AND A.BNFT_CMPNT_VRSN_NBR = (SELECT MAX(D.BNFT_CMPNT_VRSN_NBR)
			FROM BCM_BNFT_CMPNT_MSTR D
			WHERE A.BNFT_CMPNT_PARNT_SYS_ID = D.BNFT_CMPNT_PARNT_SYS_ID	)

				SELECT BNFT_CMPNT_SYS_ID, BNFT_CMPNT_NM, BNFT_CMPNT_VRSN_NBR, BNFT_CMPNT_DESC_TXT, BNFT_CMPNT_EFCTV_DT, BNFT_CMPNT_EXPIRY_DT, BNFT_CMPNT_STTS_CD, BNFT_CMPNT_PARNT_SYS_ID, LST_CHG_TMS, LST_CHG_USR, CREATD_USER_ID, CREATD_TM_STMP,BNFT_CMPNT_ST_CD
  				  FROM BCM_BNFT_CMPNT_MSTR
    			  WHERE BNFT_CMPNT_SYS_ID = {0}

SelectSQLQuery.STATIC:

SELECT * FROM (SELECT BNFT_CMPNT_SYS_ID, BNFT_CMPNT_NM, BNFT_CMPNT_DESC_TXT, BNFT_CMPNT_VRSN_NBR, 
		BNFT_CMPNT_EFCTV_DT, BNFT_CMPNT_EXPIRY_DT, BNFT_CMPNT_STTS_CD, BNFT_CMPNT_PARNT_SYS_ID,BNFT_CMPNT_TYPE
	FROM BCM_BNFT_CMPNT_MSTR 
	WHERE BCM_BNFT_CMPNT_MSTR.BNFT_CMPNT_SYS_ID IN ( 
		SELECT MAX(BNFT_CMPNT_SYS_ID) FROM BCM_BNFT_CMPNT_MSTR MSTR WHERE MSTR.BNFT_CMPNT_PARNT_SYS_ID IN (
		SELECT ED.ENTY_SYS_ID FROM ENTY_DOMN_INFO ED WHERE ED.ENTY_TYPE = 'BENEFITCOMP' AND ED.DOMN_SYS_ID IN (
		SELECT D.DOMN_SYS_ID FROM AVLBL_DOMN D WHERE D.LOB_NM {0} 
		AND D.BUS_GRP_NM {1} 
		AND D.BUS_ENTY_NM {2}
		AND D.MRKT_BUS_UNT {6} 
		)
	)
	AND
		UPPER(MSTR.BNFT_CMPNT_NM) like UPPER({3}) escape '`'
