SELECT NOTE, NOTE_NBR, CONTRACT_VAR
FROM BENNOTES  where CONTRACT ={0}
AND START_DT = to_date({1},'mm/dd/yyyy') 
AND CONTRACT_VAR = {2}

SELECT CONTRACT, START_DT, TST_PROD_IND, CREATION_USERID, CREATION_DT, LAST_UPDATED_USERID, LAST_UPDATED_DT
FROM MGRTD_DT_SGMNT
WHERE CONTRACT = {0}

          SELECT MAJOR_HEADING,NOTE FROM MAJOR_NOTES
			WHERE CONTRACT = {0} AND START_DT = to_date({1},'mm/dd/yyyy') 
			AND  MAJOR_HEADING = {2}

          SELECT MINOR_HEADING,NOTE FROM MINOR_NOTES
			WHERE CONTRACT = {0} AND START_DT = to_date({1},'mm/dd/yyyy') 
			AND  MINOR_HEADING	 = {2}
